import React from 'react';

const NewsCard = ({ article, onClick }) => {
  if (!article) {
    return null;
  }

  // Determine the sentiment color
  const sentimentColor = {
    Positive: '#28a745', // Green
    Negative: '#dc3545', // Red
    Neutral: '#6c757d',  // Gray
  };

  return (
    <div className="news-card" onClick={() => onClick(article)}>
      <div className="card-header">
        <span className="card-source">{article.original_source || 'News Source'}</span>
        <span 
          className="card-sentiment" 
          style={{ backgroundColor: sentimentColor[article.sentiment] || '#6c757d' }}
        >
          {article.sentiment}
        </span>
      </div>
      <h2 className="card-title">{article.factual_headline}</h2>
      <p className="card-original-title">Original: {article.original_title}</p>
      <div className="card-footer">
        <span>Read more...</span>
      </div>
    </div>
  );
};

export default NewsCard;
