import React from 'react';
import './ArticleDetail.css';

const ArticleDetail = ({ story, onBack }) => {
  if (!story) {
    return null;
  }

  const renderSentimentSection = (title, sentiment) => {
    if (!sentiment || !sentiment.summary) {
      return null;
    }

    return (
      <div className="sentiment-section">
        <h4>{title}</h4>
        <p className="sentiment-summary"><strong>Summary:</strong> {sentiment.summary}</p>
        {sentiment.example && (
          <blockquote className="sentiment-example">
            "{sentiment.example}"
          </blockquote>
        )}
      </div>
    );
  };

  return (
    <div className="article-detail-overlay">
      <div className="article-detail-modal">
        <button onClick={onBack} className="back-button">&larr; Back to All News</button>
        <h2>{story.factual_headline}</h2>

        <div className="core-facts">
          <h3>Core Facts</h3>
          <ul>
            {story.core_facts.map((fact, index) => (
              <li key={index}>{fact}</li>
            ))}
          </ul>
        </div>

        <div className="sentiment-analysis">
          <h3>Sentiment Analysis</h3>
          {renderSentimentSection('For', story.sentiments.for)}
          {renderSentimentSection('Neutral', story.sentiments.neutral)}
          {renderSentimentSection('Against', story.sentiments.against)}
        </div>

        <div className="sources-analyzed">
          <h3>Sources Analyzed ({story.source_articles.length})</h3>
          <ul>
            {story.source_articles.map((source, index) => (
              <li key={index}>
                <a href={source.link} target="_blank" rel="noopener noreferrer">
                  {source.title}
                </a>
              </li>
            ))}
          </ul>
        </div>
      </div>
    </div>
  );
};

export default ArticleDetail;
