import React from 'react';

function ArticleDetail({ article, onBack }) {
  if (!article) {
    return null;
  }

  // Helper to format sentiment for display
  const getSentimentClass = (sentiment) => {
    if (!sentiment) return 'neutral';
    const lowerSentiment = sentiment.toLowerCase();
    if (lowerSentiment === 'positive') return 'positive';
    if (lowerSentiment === 'negative') return 'negative';
    return 'neutral';
  };

  return (
    <div className="article-detail-container">
      <button onClick={onBack} className="back-button">
        &larr; Back to All News
      </button>
      <div className="article-detail-card">
        <h2 className="article-title">{article.original_title}</h2>
        <p className="article-source">
          Source: {article.original_source || 'N/A'}
        </p>

        <div className="details-grid">
          <div className="detail-item">
            <h3>Factual Headline</h3>
            <p>{article.factual_headline}</p>
          </div>
          <div className="detail-item">
            <h3>Sentiment</h3>
            <p className={`sentiment-badge ${getSentimentClass(article.sentiment)}`}>
              {article.sentiment || 'Neutral'}
            </p>
          </div>
        </div>

        <h3>Core Facts</h3>
        <ul className="core-facts-list">
          {article.core_facts && article.core_facts.length > 0 ? (
            article.core_facts.map((fact, index) => (
              <li key={index}>{fact}</li>
            ))
          ) : (
            <li>No facts were extracted.</li>
          )}
        </ul>

        <a 
          href={article.original_link} 
          target="_blank" 
          rel="noopener noreferrer" 
          className="original-article-link"
        >
          Read Original Article &rarr;
        </a>
      </div>
    </div>
  );
}

export default ArticleDetail;
