import React, { useState, useEffect } from 'react';
import NewsCard from './NewsCard';
import ArticleDetail from './ArticleDetail';
import './App.css';

function App() {
  const [stories, setStories] = useState([]);
  const [selectedStory, setSelectedStory] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    fetch('/api/news')
      .then(response => {
        if (!response.ok) {
          throw new Error('Network response was not ok');
        }
        return response.json();
      })
      .then(data => {
        if (!Array.isArray(data)) {
            throw new Error("Received unexpected data format from server");
        }
        const parsedStories = data.map(storyString => JSON.parse(storyString));
        setStories(parsedStories);
        setLoading(false);
      })
      .catch(error => {
        console.error("Fetch error:", error);
        setError(error.toString());
        setLoading(false);
      });
  }, []);

  const handleCardClick = (story) => {
    setSelectedStory(story);
  };

  const handleBackClick = () => {
    setSelectedStory(null);
  };

  if (loading) {
    return <div className="loading-container"><h1>Loading News Analysis...</h1></div>;
  }

  if (error) {
    return <div className="error-container"><h1>Error</h1><p>{error}</p></div>;
  }

  return (
    <div className="App">
      <header className="App-header">
        <h1>Facts First AI News</h1>
        <p>Your daily briefing, stripped of bias and clustered by story.</p>
      </header>
      <main>
        {selectedStory ? (
          <ArticleDetail story={selectedStory} onBack={handleBackClick} />
        ) : (
          <div className="news-grid">
            {stories.map((story, index) => (
              <NewsCard
                key={index}
                story={story}
                onClick={() => handleCardClick(story)}
              />
            ))}
          </div>
        )}
      </main>
    </div>
  );
}

export default App;
