import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Link } from 'react-router-dom';
import NewsCard from './NewsCard';
import ArticleDetail from './ArticleDetail';
import './App.css';

function App() {
  const [stories, setStories] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    fetch('/api/news')
      .then(response => {
        if (!response.ok) {
          throw new Error('Network response was not ok');
        }
        return response.json();
      })
      .then(data => {
        if (!Array.isArray(data)) {
            throw new Error("Received unexpected data format from server");
        }
        setStories(data);
        setLoading(false);
      })
      .catch(error => {
        console.error("Fetch error:", error);
        setError(error.toString());
        setLoading(false);
      });
  }, []);

  if (loading) {
    return <div className="loading-container"><h1>Loading News Analysis...</h1></div>;
  }

  if (error) {
    return <div className="error-container"><h1>Error</h1><p>{error}</p></div>;
  }

  return (
    <Router>
      <div className="App">
        <header className="App-header">
          <Link to="/" style={{ textDecoration: 'none', color: 'inherit' }}>
            <h1>Facts First AI News</h1>
            <p>Your daily briefing, stripped of bias and clustered by story.</p>
          </Link>
        </header>
        <main>
          <Routes>
            <Route path="/" element={
              <div className="news-grid">
                {stories.map((story, index) => (
                  <NewsCard story={story} index={index} key={index} />
                ))}
              </div>
            } />
            <Route path="/story/:index" element={<ArticleDetail stories={stories} />} />
          </Routes>
        </main>
      </div>
    </Router>
  );
}

export default App;
