// /frontend/src/ArticleDetail.js

import React from 'react';
import { useParams } from 'react-router-dom'; // Import the useParams hook
import './ArticleDetail.css';

/**
 * A small, reusable component for displaying a single sentiment analysis section.
 */
const SentimentSection = ({ title, sentimentData }) => {
  // Guard against missing data
  if (!sentimentData || !sentimentData.summary) {
    return null;
  }

  return (
    <div className="sentiment-section">
      <h4>{title}</h4>
      <p className="sentiment-summary">
        <strong>Summary:</strong> {sentimentData.summary}
      </p>
      {/* The backend provides an array of example strings */}
      {sentimentData.examples && sentimentData.examples.length > 0 && (
        <blockquote className="sentiment-example">
          "{sentimentData.examples[0]}"
        </blockquote>
      )}
    </div>
  );
};

// The component receives the entire list of stories
const ArticleDetail = ({ stories, onBack }) => {
  const { index } = useParams(); // Get the index from the URL (e.g., "/story/2")
  const story = stories[index]; // Select the correct story from the array

  // If the story doesn't exist (e.g., data hasn't loaded yet or bad URL), show a message.
  if (!story) {
    return <div>Story not found or is loading...</div>;
  }

  // Destructure with default values to prevent errors if a key is missing
  const {
    factual_headline = 'No Headline',
    core_facts = [],
    sentiments = {},
    source_articles = [],
  } = story;

  return (
    <div className="article-detail-overlay">
      <div className="article-detail-modal">
        <button onClick={onBack} className="back-button">
          &larr; Back to All News
        </button>
        <h2>{factual_headline}</h2>

        <div className="core-facts">
          <h3>Core Facts</h3>
          {core_facts.length > 0 ? (
            <ul>
              {/* Use the index as a key to prevent duplicate key errors */}
              {core_facts.map((fact, index) => (
                <li key={index}>{fact}</li>
              ))}
            </ul>
          ) : (
            <p>No core facts were extracted for this story.</p>
          )}
        </div>

        <div className="sentiment-analysis">
          <h3>Sentiment Analysis</h3>
          {/* Use the more descriptive 'positive' and 'negative' keys */}
          <SentimentSection title="Positive" sentimentData={sentiments.for} />
          <SentimentSection title="Neutral" sentimentData={sentiments.neutral} />
          <SentimentSection title="Negative" sentimentData={sentiments.against} />
        </div>

        <div className="sources-analyzed">
          <h3>Sources Analyzed ({source_articles.length})</h3>
          {source_articles.length > 0 ? (
            <ul>
              {/* Use the index as a stable, unique key */}
              {source_articles.map((source, index) => (
                <li key={index}>
                  <a href={source.link} target="_blank" rel="noopener noreferrer">
                    {source.title}
                  </a>
                </li>
              ))}
            </ul>
          ) : (
            <p>Source information is not available.</p>
          )}
        </div>
      </div>
    </div>
  );
};

export default ArticleDetail;
