import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './App.css';

function App() {
  const [news, setNews] = useState(null);

  useEffect(() => {
    axios.get('http://127.0.0.1:5000/get_news')
      .then(response => {
        setNews(response.data);
      })
      .catch(error => {
        console.error('There was an error fetching the news!', error);
      });
  }, []);

  return (
    <div className="App">
      <header className="App-header">
        <h1>Facts First AI News</h1>
        {news ? (
          <div>
            <h2>{news.article_title}</h2>
            <p>{news.article_summary}</p>
            <a href={news.article_url} target="_blank" rel="noopener noreferrer">
              Read more
            </a>
            <h3>Bias Analysis</h3>
            <p>{news.bias_analysis}</p>
            <h3>Counterpoints</h3>
            <p>{news.counterpoints}</p>
          </div>
        ) : (
          <p>Loading news...</p>
        )}
      </header>
    </div>
  );
}

export default App;
