import React from 'react';

const NewsCard = ({ news }) => {
  if (!news) {
    return null;
  }

  return (
    <div className="news-card">
      <h2>{news.article_title}</h2>
      <p>{news.article_summary}</p>
      <a href={news.article_url} target="_blank" rel="noopener noreferrer">
        Read more
      </a>
      <div className="analysis-section">
        <h3>Bias Analysis</h3>
        <p>{news.bias_analysis}</p>
      </div>
      <div className="analysis-section">
        <h3>Counterpoints</h3>
        <p>{news.counterpoints}</p>
      </div>
    </div>
  );
};

export default NewsCard;
