import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './Modern.css';
import Header from './Header';
import NewsCard from './NewsCard';
import Loading from './Loading';

function App() {
  const [news, setNews] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    setLoading(true);
    axios.get('http://127.0.0.1:5000/get_news')
      .then(response => {
        // Log the entire response to see its structure
        console.log('Data received from server:', response.data);

        // Check for the expected 'news_analysis' key, otherwise assume the data is the array
        const articles = Array.isArray(response.data.news_analysis) 
          ? response.data.news_analysis 
          : response.data;

        if (Array.isArray(articles)) {
          setNews(articles);
        } else {
          console.error("Received data is not an array:", articles);
          setError('Received unexpected data format from the server.');
        }
      })
      .catch(error => {
        console.error('There was an error fetching the news!', error);
        setError('There was an error fetching the news. Please check the server and your network connection.');
      })
      .finally(() => {
        setLoading(false);
      });
  }, []);

  return (
    <div className="container">
      <Header />
      {loading ? (
        <Loading />
      ) : error ? (
        <p className="error">{error}</p>
      ) : (
        <div className="news-container">
          {news.map((article, index) => (
            <NewsCard key={index} article={article} />
          ))}
        </div>
      )}
    </div>
  );
}

export default App;
