import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './Modern.css';
import Header from './Header';
import Loading from './Loading';
import NewsCard from './NewsCard';
import ArticleDetail from './ArticleDetail';

function App() {
    const [articles, setArticles] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const [selectedArticle, setSelectedArticle] = useState(null);

    useEffect(() => {
        axios.get('http://127.0.0.1:5001/get_news')
            .then(response => {
                console.log('Raw data received from server:', response.data);

                // The backend now sends a simple array of articles.
                if (Array.isArray(response.data)) {
                    setArticles(response.data);
                } else {
                    // This handles any unexpected, non-array response.
                    setError('Received unexpected data format from the server.');
                    console.error('Unexpected data format: Expected an array, but received:', response.data);
                }
                setLoading(false);
            })
            .catch(err => {
                console.error('Error fetching news:', err);
                setError('There was an error fetching the news. Please check the server and your network connection.');
                setLoading(false);
            });
    }, []);

    const handleCardClick = (article) => {
        setSelectedArticle(article);
    };

    const handleBackClick = () => {
        setSelectedArticle(null);
    };

    if (loading) {
        return <Loading />;
    }

    if (error) {
        return <div className="error-message">{error}</div>;
    }

    return (
        <div className="container">
            <Header />
            <main>
                {selectedArticle ? (
                    <ArticleDetail article={selectedArticle} onBackClick={handleBackClick} />
                ) : (
                    <div className="articles-grid">
                        {articles.map((article, index) => (
                            <NewsCard
                                key={index}
                                article={article}
                                onClick={handleCardClick}
                            />
                        ))}
                    </div>
                )}
            </main>
        </div>
    );
}

export default App;
